import { model } from "mongoose";
import RoomSchema from "../schema/RoomSchema";

const RoomModel = model('Rooms', RoomSchema);

export default RoomModel;

import { Schema } from "mongoose";

const RoomSchema = new Schema({
    name: {
        required: true,
        type: String,
    },
    number_of_beds: {
        required: false,
        type: Number,
    },
    available: {
        required: false,
        type: Boolean,
    },
});

export default RoomSchema;

import { Router } from "express";

import MiddlewareConfig from "../CommonTypes/MiddlewareConfig";
import RouterConfig from "../CommonTypes/RouterConfig";
import ControllerFactory from "./ControllerFactory";

function createMiddleware(middleware: MiddlewareConfig) {
    return ControllerFactory.create(middleware.type, middleware.model);
}

const RouterFactory = {
    createRoute(endpoints: RouterConfig[]) {
        const router = Router();

        endpoints.map(( endpoint ) => {
            // if()

            const middleware = createMiddleware(endpoint.middleware);

            if( endpoint.type == "GET" ){
                router.get(endpoint.uri, middleware);
            } else if( endpoint.type == "POST" ){
                router.post(endpoint.uri, middleware);
            } else if( endpoint.type == "PATCH" ){
                router.patch(endpoint.uri, middleware);
            } else if( endpoint.type == "DELETE" ){
                router.delete(endpoint.uri, middleware);
            } else {
                return("{'error': 'Error'}");
            }
        });

        return(router);
    }
}

export default RouterFactory;

import { Model } from "mongoose";

import Controllers from "../Controllers/Controllers";
import ActionType from "../CommonTypes/ActionType";

const ControllerFactory = {
    create(type: string, model: Model<any>) {
        if(!type) console.warn("Empty type");
        if(!model) console.warn("Empty model");

        return Controllers.find((item: ActionType) => item.type == type).action(model);
    }
};

export default ControllerFactory;

import { Model } from "mongoose";

import ActionType from "../CommonTypes/ActionType";
import ControllerMiddleware from "./ControllerMiddleware/ControllerMiddleware";

const Controllers: ActionType[] = [
    {
        type: "GET_BY_ID",
        action: ( model: Model<any> ) => {
            return( ControllerMiddleware.getOneById( model ));
        }
    },
    {
        type: "DELETE_BY_ID",
        action: ( model: Model<any> ) => {
            return( ControllerMiddleware.deleteOneById( model ));
        }
    },
    {
        type: "UPDATE_BY_ID",
        action: ( model: Model<any> ) => {
            return( ControllerMiddleware.updateOneById( model ));
        }
    },
    {
        type: "GET_COUNT",
        action: ( model: Model<any> ) => {
            return( ControllerMiddleware.getCount( model ));
        }
    },
    {
        type: "GET_ALL",
        action: ( model: Model<any> ) => {
            return( ControllerMiddleware.getAll( model ));
        }
    },
    {
        type: "POST_ONE",
        action: ( model: Model<any> ) => {
            return( ControllerMiddleware.postOne( model ));
        }
    },
];

export default Controllers;

import { Request, Response } from "express";
import { Model } from "mongoose";

import ServiceFactory from "../../Factories/ServiceFactory";

const ControllerMiddleware = {    
    getOneById: (model: Model<any>) => {
        const middleware = async(req: Request, res: Response, donne: any) => {
            try {
                const id = req.params.id;
                const data = await ServiceFactory.create({ type: "GET_BY_ID", body: id }, model);
                res.status( 200 ).json( data );
                donne();
            }
            catch( error: any ){
                res.status( 500 ).json({ message: error.message });
            }
        }

        return middleware;
    },
    getCount: (model: Model<any>) => {
        const middleware = async(req: Request, res: Response, donne: any) => {
            try {
                const data = await ServiceFactory.create({ type: "GET_COUNT", body: "" }, model);
                res.status( 200 ).json( data );
                donne();
            }
            catch( error: any ){
                res.status( 500 ).json({ message: error.message });
            }
        }

        return middleware;
    },
    postOne: (model: Model<any>) => {
        const middleware = async(req: Request, res: Response, donne: any) => {
            try {
                const data = await ServiceFactory.create({ type: "POST_ONE", body: req.body }, model);
                res.status( 200 ).json( data );
                donne();
            }
            catch( error: any ){
                res.status( 500 ).json({ message: error.message });
            }
        }

        return middleware;
    },
    getAll: (model: Model<any>) => {
        const middleware = async(req: Request, res: Response, donne: any) => {
            try {
                const data = await ServiceFactory.create({ type: "GET_ALL", body: "" }, model);
                res.status( 200 ).json( data );
                donne();
            }
            catch( error: any ){
                res.status( 500 ).json({ message: error.message });
            }
        }

        return middleware;
    },
    deleteOneById: (model: Model<any>) => {
        const middleware = async(req: Request, res: Response, donne: any) => {
            try {
                const id = req.params.id;
                const data = await ServiceFactory.create({ type: "DELETE_BY_ID", body: id }, model);
                res.status( 200 ).json( data );
                donne();
            }
            catch( error: any ){
                res.status( 500 ).json({ message: error.message });
            }
        }

        return middleware;
    },
    updateOneById: (model: Model<any>) => {
        const middleware = async(req: Request, res: Response) => {
            try {
                const id = req.params.id;
                const updatedData = req.body;
                const options = { new: true };

                if( !id ) {
                    throw new Error("No ID is provided for the updateById function");
                }

                if( !updatedData ) {
                    throw new Error("No updated data is provided for the updateById function");
                }

                const body = { id: id, updatedData: updatedData, options: options};

                const result = await ServiceFactory.create({ type: "UPDATE_BY_ID", body: body }, model);

                res.status(200).send(result)
            }
            catch (error: any) {
                res.status(500).json({ message: error.message })
            }
        }

        return middleware;
    },
}

export default ControllerMiddleware;

import { Model } from "mongoose";

 import ActionType from '../CommonTypes/ActionType';
 import Actions from '../Actions/Actions';
 import EndpointType from "../CommonTypes/EndpointType";

 const ServiceFactory = {
     create(endpoint: EndpointType, model: Model<any>) {
         if( Actions.find((item: ActionType) => item.type == endpoint.type) ) {
             return Actions.find((item: ActionType) => item.type == endpoint.type).action(model, endpoint.body);
         }
         return("{'error': 'Error'}");
     }
 }

 export default ServiceFactory;

import { Model } from "mongoose";

const Actions = [
    {
        type: "GET_BY_ID",
        action: (model: Model<any>, id: string) => {
            return(model.findById( id ));
        },
    },
    {
        type: "GET_ALL",
        action: (model: Model<any>, id: string) => {
            return(model.find());
        },
    },
    {
        type: "GET_COUNT",
        action: (model: Model<any>, id: string) => {
            return(model.count());
        },
    },
    {
        type: "DELETE_BY_ID",
        action: (model: Model<any>, id: string) => {
            return(model.findByIdAndDelete( id ));
        },
    },
    {
        type: "UPDATE_BY_ID",
        action: (model: Model<any>, body: any) => {
            return(model.findByIdAndUpdate( body.id, body.updatedData, body.options ));
        },
    },
    {
        type: "POST_ONE",
        action: async (model: Model<any>, body: any) => {
            const data = new model(body);
            const res  = await data.save();
            return({ id: res._id });
        },
    },
];

export default Actions;
